import React, { useState, useEffect } from "react";
import "../Card/card.css";
import { Tooltip } from "@material-ui/core";
import axios from "axios";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";

const Card = () => {
  const [albums, setAlbums] = useState([]);
  const [expanded, setExpanded] = useState(false);
  const [filteredAlbums, setFilteredAlbums] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get("https://qtify-backend-labs.crio.do/albums");
      const simplifiedData = response.data.map((album) => ({
        id: album.id,
        image: album.image,
        follows: album.follows,
        title: album.title,
      }));
      setAlbums(simplifiedData);
      setFilteredAlbums(simplifiedData);
      setIsLoading(false);
    } catch (error) {
      console.error("Error fetching albums:", error);
    }
  };

  const handleToggleExpand = () => {
    setExpanded(!expanded);
  };

  const filterAlbums = (genre) => {
    if (genre === "ALL") {
      setFilteredAlbums(albums);
    } else {
      const filteredData = albums.filter((album) =>
        album.genre.key === genre.toLowerCase()
      );
      setFilteredAlbums(filteredData);
    }
  };

  const responsive = {
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 7,
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2,
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1,
    },
  };

  if (isLoading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="App">
      <div className="heading1">
        <div>Albums</div>
        {albums.length > 7 && (
          <div onClick={handleToggleExpand} className="headingSub">
            {expanded ? "Collapse" : "Show All"}
          </div>
        )}
      </div>

      <Carousel
        className="card-container"
        responsive={responsive}
        showDots={false}
        partialVisible={true}
        infinite={true}
      >
        {filteredAlbums.map((album) => (
          <div key={album.id} className="card">
            <Tooltip title={`${65} Songs`} placement="top">
              <img src={album.image} alt="cardImg" />
            </Tooltip>
            <div>
              <p className="card-follows">{album.follows} Follows</p>
            </div>
            <p className="card-title">{album.title}</p>
          </div>
        ))}
      </Carousel>
    </div>
  );
};

export default Card;
