import React, { useState, useEffect } from "react";
import "./filter.css";
import { Tabs, Tab, Box } from "@material-ui/core";
import axios from "axios";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";

const Filter = () => {
  const [value, setValue] = useState(0);
  const [albums, setAlbums] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get("https://qtify-backend-labs.crio.do/albums");
      const simplifiedData = response.data.map((album) => ({
        id: album.id,
        image: album.image,
        follows: album.follows,
        title: album.title,
        genre: album.genre,
      }));
      setAlbums(simplifiedData);
      setIsLoading(false);
    } catch (error) {
      console.error("Error fetching albums:", error);
    }
  };

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  const filterAlbums = (genre) => {
    if (genre === "ALL") {
      setValue(0);
    } else {
      const index = albums.findIndex((album) => album.genre.key === genre.toLowerCase());
      if (index !== -1) {
        setValue(index + 1);
      }
    }
  };

  const tabItems = [
    { label: "ALL", genre: "ALL" },
    { label: "Jazz", genre: "jazz" },
    { label: "Rock", genre: "rock" },
    { label: "Pop", genre: "pop" },
    { label: "Blues", genre: "blues" },
  ];

  const responsive = {
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 7,
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2,
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1,
    },
  };

  if (isLoading) {
    return <div>Loading...</div>;
  }

  return (
    <Box sx={{ width: "100%" }}>
      <hr />
      <h2 className="title">Albums</h2>
      <Tabs
        value={value}
        onChange={handleChange}
        sx={{
          "& button": { color: "white" },
          "& button.Mui-selected": { color: "white" },
        }}
        TabIndicatorProps={{
          style: {
            backgroundColor: "green",
          },
        }}
      >
        {tabItems.map((item, index) => (
          <Tab key={index} label={item.label} onClick={() => filterAlbums(item.genre)} />
        ))}
      </Tabs>

      <Carousel
        className="card-container"
        responsive={responsive}
        showDots={false}
        partialVisible={true}
        infinite={true}
        selected={value}
      >
        {albums.map((album) => (
          <div key={album.id} className="card">
            <img src={album.image} alt="cardImg" />
            <div>
              <p className="card-follows">{album.follows} Follows</p>
            </div>
            <p className="card-title">{album.title}</p>
          </div>
        ))}
      </Carousel>

      <hr />
    </Box>
  );
};

export default Filter;
