import React, { useState, useEffect } from "react";
import { Tabs, Tab, Box } from "@material-ui/core";
import "./filter.css";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
import Card from "../Card/card";
const Filter = () => {
  const [value, setValue] = useState(0);
  const [data, setData] = useState([]);
  const [genre, setGenre] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const responseSongs = await fetch("https://qtify-backend-labs.crio.do/songs");
        const songsData = await responseSongs.json();
        setData(songsData);

        const responseGenres = await fetch("https://qtify-backend-labs.crio.do/genres");
        const genresData = await responseGenres.json();
        setGenre(genresData);

        setIsLoading(false);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  if (isLoading) {
    return <div>Loading...</div>;
  }

  const jazz = data.filter((e) => e.genre.key === "jazz");
  const rock = data.filter((e) => e.genre.key === "rock");
  const pop = data.filter((e) => e.genre.key === "pop");
  const blues = data.filter((e) => e.genre.key === "blues");

  const tabItems = [
    { label: "ALL", data: data },
    { label: "Jazz", data: jazz },
    { label: "Rock", data: rock },
    { label: "Pop", data: pop },
    { label: "Blues", data: blues },
  ];

  const renderCards = (items) => {
    return items.map((item) => (
      <Card key={item.id} album={item} />
    ));
  };


  const TabPanel = ({ children, value, index }) => {
    return value === index ? <Box sx={{ p: 3 }}>{children}</Box> : null;
  };

  const responsive = {
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 7,
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2,
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1,
    },
  };


  return (
    <Box sx={{ width: "100%" }}>
      <hr />
      <h2 className="title">Songs</h2>
      <Tabs
        value={value}
        onChange={handleChange}
        sx={{
          "& button": { color: "white" },
          "& button.Mui-selected": { color: "white" },
        }}
        TabIndicatorProps={{
          style: {
            backgroundColor: "green",
          },
        }}
      >
        {tabItems.map((item, index) => (
          <Tab key={index} label={item.label} />
        ))}
      </Tabs>
      {tabItems.map((item, index) => (
        <TabPanel key={index} value={value} index={index}>
          {/* Render the cards using carousel */}
          <Carousel showDots={true} responsive={responsive}>
            {renderCards(item.data)}
          </Carousel>
        </TabPanel>
      ))}
      <hr />
    </Box>
  );
};

export default Filter;
