import React, { useState, useEffect } from "react";
import "./card.css";
import axios from "axios";


const Card = () => {

  const [topAlbums, setTopAlbums] = useState([]);

  useEffect(() => {
    fetchTopAlbums();
  }, []);

  const fetchTopAlbums = async () => {
    try {
      const response = await axios.get(
        "https://qtify-backend-labs.crio.do/albums/top"
      );
      const simplifiedData = response.data.map((album) => ({
        id: album.id,
        image: album.image,
        follows: album.follows,
        title: album.title,
      }));
      setTopAlbums(simplifiedData);
    } catch (error) {
      console.error("Error fetching top albums:", error);
    }
  };

  return (
    <div className="grid">
      <div className="heading1">
        <div>Top Albums</div>
      </div>
      <div className="card">
        {topAlbums.map((album) => (
          <div key={album.id}>
            <img style={{
                  borderRadius: "10px",
                  display: "block",
                  width: "200px",
                  height: "220px"
                }}src={album.image} alt="cardImg" />
            <div>
              <p style={{
                    display: "flex",
                    backgroundColor: "black",
                    color: "white",
                    borderRadius: "12px",
                    margin: "0px",
                    padding: "8px"
                  }}>{album.follows} Follows</p>
            </div>
            <p>{album.title}</p>
          </div>
        ))}
      </div>
    </div>
  );  










};

export default Card;
