import React, { useState, useEffect } from "react";
import "./card.css";
import { Tooltip } from "@material-ui/core";
import axios from "axios";

const Card = () => {
  const [topAlbums, setTopAlbums] = useState([]);
  const [newAlbums, setNewAlbums] = useState([]);

  useEffect(() => {
    fetchTopAlbums();
  }, []);

  useEffect(() => {
    fetchNewAlbums();
  }, []);

  const fetchNewAlbums = async () => {
    try {
      const response = await axios.get(
        "https://qtify-backend-labs.crio.do/albums/new"
      );
      const simplifiedData = response.data.map((album) => ({
        id: album.id,
        image: album.image,
        follows: album.follows,
        title: album.title,
      }));
      setNewAlbums(simplifiedData);
    } catch (error) {
      console.error("Error fetching new albums:", error);
    }
  };

  const fetchTopAlbums = async () => {
    try {
      const response = await axios.get(
        "https://qtify-backend-labs.crio.do/albums/top"
      );
      const simplifiedData = response.data.map((album) => ({
        id: album.id,
        image: album.image,
        follows: album.follows,
        title: album.title,
      }));
      setTopAlbums(simplifiedData);
    } catch (error) {
      console.error("Error fetching top albums:", error);
    }
  };

  return (
    <div className="grid">
      <div className="heading1">Top Albums</div>
      <div className="card-container">
        {topAlbums.map((album) => (
          <div key={album.id} className="card">
            <Tooltip title={`${65} Songs`} placement="top">
              <img src={album.image} alt="cardImg" />
            </Tooltip>
            <div>
              <p className="card-follows">{album.follows} Follows</p>
            </div>
            <p className="card-title">{album.title}</p>
          </div>
        ))}
        <div className="show-all-button">Show All</div>
      </div>
    </div>
  );
};

export default Card;
