import React, { useState, useEffect } from "react";
import "./card.css";
import axios from "axios";


const Card = () => {

  const [topAlbums, setTopAlbums] = useState([]);
  const [newAlbums, setNewAlbums] = useState([]);

  useEffect(() => {
    fetchTopAlbums();
  }, []);

  useEffect(() => {
    fetchNewAlbums();
  }, []);

  const fetchNewAlbums = async () => {
    try {
      const response = await axios.get(
        "https://qtify-backend-labs.crio.do/albums/new"
      );
      const simplifiedData = response.data.map((album) => ({
        id: album.id,
        image: album.image,
        follows: album.follows,
        title: album.title,
      }));
      setNewAlbums(simplifiedData);
    } catch (error) {
      console.error("Error fetching top albums:", error);
    }
  };


  const fetchTopAlbums = async () => {
    try {
      const response = await axios.get(
        "https://qtify-backend-labs.crio.do/albums/top"
      );
      const simplifiedData = response.data.map((album) => ({
        id: album.id,
        image: album.image,
        follows: album.follows,
        title: album.title,
      }));
      setTopAlbums(simplifiedData);
    } catch (error) {
      console.error("Error fetching top albums:", error);
    }
  };

  return (
    <div className="grid">
      <div className="heading1">
        <div>Top Albums</div>
      </div>
      <div className="card-container">
        {topAlbums.map((album) => (
          <div key={album.id} className="card" style={{ height: "205px", width: "159px", borderRadius: "10px" }}>
            <img
              style={{
                borderRadius: "10px",
                display: "block",
                width: "159px",
                height: "170px"
              }}
              src={album.image}
              alt="cardImg"
            />
            <div>
              <p style={{
                    display: "flex",
                    backgroundColor: "black",
                    color: "white",
                    borderRadius: "20px",
                    margin: "0px",
                    padding: "5px"
                  }}>{album.follows} Follows</p>
            </div>
            <p style={{
              width: "159px",color: "white"
            }}>{album.title}</p>
            <br></br>
          </div>
        ))}
      </div>
<br></br>

      <div className="heading1">
        <div>New Albums</div>
      </div>
      <div className="card-container">
        {newAlbums.map((album) => (
          <div key={album.id} className="card" style={{ height: "205px", width: "159px", borderRadius: "10px" }}>
            <img
              style={{
                borderRadius: "10px",
                display: "block",
                width: "159px",
                height: "170px"
              }}
              src={album.image}
              alt="cardImg"
            />
            <div>
              <p style={{
                    display: "flex",
                    backgroundColor: "black",
                    color: "white",
                    borderRadius: "20px",
                    margin: "0px",
                    padding: "5px"
                  }}>{album.follows} Follows</p>
            </div>
            <p style={{
  width: "159px",
  color: "white",
  fontSize: "18px"
}}>{album.title}</p>
          </div>
        ))}
      </div>
    </div>
  );










};

export default Card;
