import React, { useState, useEffect } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/swiper.min.css";
import "swiper/modules/navigation/navigation.min.css";
import "swiper/modules/pagination/pagination.min.css";
import SwiperCore, { Navigation, Pagination } from "swiper/core";
import { Tooltip } from "@material-ui/core";
import axios from "axios";
import "./card.css";

SwiperCore.use([Navigation, Pagination]);



const Card = () => {

  const [topAlbums, setTopAlbums] = useState([]);
  const [newAlbums, setNewAlbums] = useState([]);

  useEffect(() => {
    fetchTopAlbums();
  }, []);

  useEffect(() => {
    fetchNewAlbums();
  }, []);

  const fetchNewAlbums = async () => {
    try {
      const response = await axios.get(
        "https://qtify-backend-labs.crio.do/albums/new"
      );
      const simplifiedData = response.data.map((album) => ({
        id: album.id,
        image: album.image,
        follows: album.follows,
        title: album.title,
      }));
      setNewAlbums(simplifiedData);
    } catch (error) {
      console.error("Error fetching top albums:", error);
    }
  };


  const fetchTopAlbums = async () => {
    try {
      const response = await axios.get(
        "https://qtify-backend-labs.crio.do/albums/top"
      );
      const simplifiedData = response.data.map((album) => ({
        id: album.id,
        image: album.image,
        follows: album.follows,
        title: album.title,
      }));
      setTopAlbums(simplifiedData);
    } catch (error) {
      console.error("Error fetching top albums:", error);
    }
  };

    return (
      <div className="grid">
        <div className="heading1">Top Albums</div>
        <Swiper
          navigation
          slidesPerView={0}
          breakpoints={{
            640: {
              slidesPerView: 2,
            },
            768: {
              slidesPerView: 3,
            },
            1024: {
              slidesPerView: 4,
            },
          }}
        >
          {topAlbums.map((album) => (
            <SwiperSlide key={album.id}>
              <div className="card">
                <Tooltip title={`${65} Songs`} placement="top">
                  <img src={album.image} alt="cardImg" />
                </Tooltip>
                <div>
                  <p className="card-follows">{album.follows} Follows</p>
                </div>
                <p className="card-title">{album.title}</p>
              </div>
            </SwiperSlide>
          ))}
        </Swiper>
      </div>
    );
  };

export default Card;
