import React, { useState, useEffect } from "react";
import { Tabs, Tab, Box } from "@material-ui/core";
import "./filter.css";

const TabPanel = ({ children, value, index }) => {
  return value === index ? <Box sx={{ p: 3 }}>{children}</Box> : null;
};

const Filter = () => {
  const [value, setValue] = useState(0);
  const [data, setData] = useState([]);
  const [genre, setGenre] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const responseSongs = await fetch("https://qtify-backend-labs.crio.do/songs");
        const songsData = await responseSongs.json();
        setData(songsData);

        const responseGenres = await fetch("https://qtify-backend-labs.crio.do/genres");
        const genresData = await responseGenres.json();
        setGenre(genresData);

        setIsLoading(false);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  if (isLoading) {
    return <div>Loading...</div>;
  }

  // Filter songs based on genre
  const filteredData = (genreKey) =>
    data.filter((song) => song.genre.key === genreKey);

  const tabItems = [
    { label: "ALL", data: data },
    { label: "Jazz", data: filteredData("jazz") },
    { label: "Rock", data: filteredData("rock") },
    { label: "Pop", data: filteredData("pop") },
    { label: "Blues", data: filteredData("blues") },
  ];

  return (
    <Box sx={{ width: "100%" }}>
      <hr />
      <h2 className="title">Songs</h2>
      <Tabs
        value={value}
        onChange={handleChange}
        sx={{
          "& button": { color: "white" },
          "& button.Mui-selected": { color: "white" },
        }}
        TabIndicatorProps={{
          style: {
            backgroundColor: "green",
          },
        }}
      >
        {tabItems.map((item, index) => (
          <Tab key={index} label={item.label} />
        ))}
      </Tabs>

      {tabItems.map((item, index) => (
        <TabPanel key={index} value={value} index={index}>
          {item.data.map((song) => (
            <div key={song.id}>{song.title}</div>
          ))}
        </TabPanel>
      ))}

      <hr />
    </Box>
  );
};

export default Filter;
