import React, { useState, useEffect } from "react";
import "./card.css";
import { Tooltip } from "@material-ui/core";
import axios from "axios";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
import AlbumFilter from "./AlbumFilter";

const Card = () => {
  const [topAlbums, setTopAlbums] = useState([]);
  const [newAlbums, setNewAlbums] = useState([]);
  const [filteredTopAlbums, setFilteredTopAlbums] = useState([]);
  const [filteredNewAlbums, setFilteredNewAlbums] = useState([]);
  const [genres, setGenres] = useState([]);
  const [activeGenre, setActiveGenre] = useState("All");


  useEffect(() => {
    fetchTopAlbums();
    fetchNewAlbums();
    fetchGenres();
  }, []);

  const fetchTopAlbums = async () => {
    try {
      const response = await axios.get(
        "https://qtify-backend-labs.crio.do/albums/top"
      );
      const simplifiedData = response.data.map((album) => ({
        id: album.id,
        image: album.image,
        follows: album.follows,
        title: album.title,
      }));
      setTopAlbums(simplifiedData);
      setFilteredTopAlbums(simplifiedData);
    } catch (error) {
      console.error("Error fetching top albums:", error);
    }
  };

  const fetchNewAlbums = async () => {
    try {
      const response = await axios.get(
        "https://qtify-backend-labs.crio.do/albums/new"
      );
      const simplifiedData = response.data.map((album) => ({
        id: album.id,
        image: album.image,
        follows: album.follows,
        title: album.title,
      }));
      setNewAlbums(simplifiedData);
      setFilteredNewAlbums(simplifiedData);
    } catch (error) {
      console.error("Error fetching new albums:", error);
    }
  };

  const fetchGenres = async () => {
    try {
      const response = await axios.get(
        "https://qtify-backend-labs.crio.do/genres"
      );
      const genres = response.data.map((genre) => genre.name);
      setGenres(genres);
    } catch (error) {
      console.error("Error fetching genres:", error);
    }
  };

  const handleFilterChange = (genre) => {
    setActiveGenre(genre);

    // Filter top albums based on genre
    if (genre === "All") {
      setFilteredTopAlbums(topAlbums);
    } else {
      const filteredTopAlbums = topAlbums.filter(
        (album) => album.genre === genre
      );
      setFilteredTopAlbums(filteredTopAlbums);
    }

    // Filter new albums based on genre
    if (genre === "All") {
      setFilteredNewAlbums(newAlbums);
    } else {
      const filteredNewAlbums = newAlbums.filter(
        (album) => album.genre === genre
      );
      setFilteredNewAlbums(filteredNewAlbums);
    }
  };

  const responsive = {
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 7,
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2,
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1,
    },
  };

  return (
    <div className="App">
      <AlbumFilter genres={genres} onFilter={handleFilterChange} />

      <div className="heading1">
        <div>Top Albums</div>
        {filteredTopAlbums.length > 7 && (
          <div onClick={handleToggleTopExpand} className="headingSub">
            {topExpanded ? "Collapse" : "Show All"}
          </div>
        )}
      </div>

      <Carousel
        className="card-container"
        responsive={responsive}
        showDots={false}
        partialVisible={true}
        infinite={true}
      >
        {filteredTopAlbums.map((album) => (
          <div key={album.id} className="card">
            <Tooltip title={`${65} Songs`} placement="top">
              <img src={album.image} alt="cardImg" />
            </Tooltip>
            <div>
              <p className="card-follows">{album.follows} Follows</p>
            </div>
            <p className="card-title">{album.title}</p>
          </div>
        ))}
      </Carousel>

      <div className="heading1">
        <div>New Albums</div>
        {filteredNewAlbums.length > 7 && (
          <div onClick={handleToggleNewExpand} className="headingSub">
            {newExpanded ? "Collapse" : "Show All"}
          </div>
        )}
      </div>

      <Carousel
        className="card-container"
        responsive={responsive}
        showDots={false}
        partialVisible={true}
        infinite={true}
      >
        {filteredNewAlbums.map((album) => (
          <div key={album.id} className="card">
            <Tooltip title={`${65} Songs`} placement="top">
              <img src={album.image} alt="cardImg" />
            </Tooltip>
            <div>
              <p className="card-follows">{album.follows} Follows</p>
            </div>
            <p className="card-title">{album.title}</p>
          </div>
        ))}
      </Carousel>
    </div>
  );
};

export default Card;
