import React, { useEffect, useState } from "react";
import { Tooltip } from "@material-ui/core";
import "./card.css";

const Card = ({ imageSrc, followersCount, title, songsCount }) => {
  return (
    <div className="card">
      <Tooltip anchorSelect="card" title={`${songsCount} Songs`} placement="top">
        <div className="card-content">
          <img src={imageSrc} alt={title} className="card-image" />
          <p className="card-followers">{followersCount} Follows</p>
        </div>
      </Tooltip>
      <h2 className="card-title">{title}</h2>
    </div>
  );
};

const CardList = () => {
  const [cards, setCards] = useState([]);

  useEffect(() => {
    fetch("https://qtify-backend-labs.crio.do/albums/top")
      .then((response) => response.json())
      .then((data) => setCards(data))
      .catch((error) => console.log(error));
  }, []);

  return (
    <div>
      {cards.map((card) => (
        <Card
          key={card.id}
          imageSrc={card.imageSrc}
          followersCount={card.followersCount}
          title={card.title}
          songsCount={card.songsCount}
        />
      ))}
    </div>
  );
};

export default CardList;
